var coresupport={};
coresupport.has=function(jsn,key){
	if(typeof(key)=="undefined")
	return jsn!=null && typeof(jsn)!="undefined" ;
	else
	return jsn!=null && typeof(jsn[key])!="undefined" ; 
}
coresupport.ifUndefined=function(jsn,nval,nval1){
	
	if(!_pf.has(nval1)){ 
		if(_pf.has(jsn)) return jsn;
		else return nval;
	}else{
		if(_pf.has(jsn,nval)) return jsn[nval];
		else return nval1;
	}
		
}
coresupport.each=function(jsn,func,boll){
var re_loop=[];
	var ctt=0;
	var ctt_e=0;
	var boll_d=boll||true;

if(typeof(jsn)==="object"){

	for(var ins in jsn){
		ctt_e++;
	try{
if(coresupport.has(func)){
		
	if(typeof(jsn[ins])!="function" && boll_d==true){
		
		func(ins,jsn[ins],ctt);
		ctt++;
	}	
	
	}else{
		if(typeof(jsn[ins])!="function" && boll_d==true){
	re_loop[ins]=jsn[ins];
		}
	}
	}
	catch(e){
		console.log(e);
	}
}

}
else if(typeof(jsn)==="string"){
	if(coresupport.has(func)){
		if(typeof(jsn)!="function" && boll_d==true){
		re_loop[0]=jsn;
	func(0,jsn,ctt);
		}
		}else{
			if(typeof(jsn)!="function" && boll_d==true){
		re_loop[0]=jsn;
			}
		}
}


	if(!coresupport.has(func)){
		return re_loop;
	}
};
exports.helper=coresupport;